mapper.select:
select count(id) from sys_user_info where 1=1

  AND no like  CONCAT('%', #{studentNo,jdbcType=VARCHAR}, '%')


  AND realname like  CONCAT('%', #{name,jdbcType=VARCHAR}, '%')


  AND department = #{name,jdbcType=BIGINT}


  AND major = #{major,jdbcType=BIGINT}


  AND classes = #{classes,jdbcType=BIGINT}

select

from sys_user_info where 1=1

  AND no like  CONCAT('%', #{studentNo,jdbcType=VARCHAR}, '%')


  AND realname like  CONCAT('%', #{name,jdbcType=VARCHAR}, '%')


  AND department = #{name,jdbcType=BIGINT}


  AND major = #{major,jdbcType=BIGINT}


  AND classes = #{classes,jdbcType=BIGINT}

order by id DESC limit #{startRecords},#{pageSize}
